class VerifyCompositions:

    def __init__(self):
        self.df_PCD = pd.read_csv(path + '\\PCD_valid_formulae.csv')
        self.PCD_list = []
        self._parse_PCD()

    def _parse_PCD(self):
        for formula in self.df_PCD['formula']:
            try:
                self.PCD_list.append(mg.Composition(
                        formula).fractional_composition.as_dict())
            except:
                pass

    def verify_df(self, df):
        print('warning: the function "verify_df" is being depricated for new function "verifty_df_PCD"')
        valid_list = []
        for formula in df['formula']:
            f1 = mg.Composition(formula).fractional_composition.as_dict()
            if f1 in self.PCD_list:
                valid_list.append(formula)
        df_PCD_verified = df[df['formula'].isin(valid_list)]
        return df_PCD_verified

    def verify_df_PCD(self, df):
        valid_list = []
        for formula in df['formula']:
            f1 = mg.Composition(formula).fractional_composition.as_dict()
            if f1 in self.PCD_list:
                valid_list.append(formula)
        df_PCD_verified = df[df['formula'].isin(valid_list)]
        return df_PCD_verified

    def match_df_compositions(self, df1, df2):
        '''
        Compares the fractional composition between the formula in two dataframes.

        Parameters
        ----------
        df1: pd.DataFrame()
            DataFrame with a 'formula' column
        df2: pd.DataFrame()
            vector containing column names 'formula'

        Return
        ----------
        df1, df2: original dataframes limited to rows that had matched formula
        '''
        # copy the data frames so we don't overide old dataframe
        df1 = df1.copy()
        df1.reset_index(drop=True, inplace=True)

        df2 = df2.copy()
        df2.reset_index(drop=True, inplace=True)

        # find the franctional composition for all formula and store in df
        for j in range(len(df1)):
            formula1 = df1['formula'].loc[j]
            c1 = mg.Composition(formula1).fractional_composition.as_dict()
            df1.loc[j, 'comp'] = str(c1)
        try:
            for k in range(len(df2)):
                formula2 = df2['formula'].loc[k]
                c2 = mg.Composition(formula2).fractional_composition.as_dict()
                df2.loc[k, 'comp'] = str(c2)
        except:
            print('COMPOSITION ERROR: ', formula2, k)

        # remove duplicates in the fractional composition
        df1.drop_duplicates(subset=['comp'], keep=False, inplace=True)
        df2.drop_duplicates(subset=['comp'], keep=False, inplace=True)

        # compare dataframes and return the matching indexis
        df1 = df1[df1['comp'].isin(df2['comp'].values)]
        df2 = df2[df2['comp'].isin(df1['comp'].values)]

        # make sure both dataframes match up by sorting on the matched value
        df1.sort_values(by=['comp'], inplace=True)
        df2.sort_values(by=['comp'], inplace=True)

        df1.reset_index(drop=True, inplace=True)
        df2.reset_index(drop=True, inplace=True)

        # make a new dataframe that holds the formula and experimental values
        df_1 = pd.DataFrame()
        df_1['formula'] = df1['formula'].loc[df1['comp'] == df2['comp']]
        df_1 = df1.loc[df2['comp'] == df1['comp']]

        # make a dataframe that holds the DFT values on the same index as df above
        df_2 = pd.DataFrame()
        df_2['formula'] = df2['formula'].loc[df1['comp'] == df2['comp']]
        df_2 = df2.loc[df1['comp'] == df2['comp']]

        return df_1, df_2


class MaterialsProjectData:

    def __init__(self):
        numerical_properties = [             'Band Gap',
                     'Bulk Modulus, Reuss',
                     'Bulk Modulus, Voigt',
                     'Bulk Modulus, Voigt-Reuss-Hill',
                     'Density',
                     'Elastic Anisotropy',
                     'Energy Above Convex Hull',
                     'Enthalpy of Formation',
                     'Number of Elements',
                     'Piezoelectric Modulus',
                     "Poisson's Ratio",
                     'Shear Modulus, Reuss',
                     'Shear Modulus, Voigt',
                     'Shear Modulus, Voigt-Reuss-Hill',
                     'Space Group Number',
                     'Total Magnetization',
                     'Unit Cell Volume',
                     'VASP Energy for Structure']

        mp_df_dict ={}
        N = 0
        jsons = []

        for i in range(1,11):
            data_sheet = path + r'\jsons/mp_all_pif-merged-' + str(i) + '.json'
            jsons.append(data_sheet)

        for data_sheet in jsons:
            mp_raw_df = pd.read_json(data_sheet)
            for formula, properties in zip(mp_raw_df['chemicalFormula'], mp_raw_df['properties']):
                mp_df_dict[N] = {}
                for item in properties:
                    if item['name'] in numerical_properties:
                        if item['scalars'] > 0:
                            mp_df_dict[N]['formula'] = formula
                            mp_df_dict[N][item['name']] = float(item['scalars'])
                N += 1
        self.mp_df = pd.DataFrame(mp_df_dict).transpose()

    def get_property_df(self, property_of_interest):

        # create a dataframe to hold desired property
        property_df = pd.DataFrame()

        # read compositions and properties from materials project dataframe
        property_df['formula'] = self.mp_df['formula'].astype(str)
        property_df['target'] = self.mp_df[property_of_interest].astype(float)

        # remove missing property values
        property_df.dropna(subset=['target'], inplace=True)

        # get mean value for duplicates
        property_df = property_df.groupby('formula').mean().reset_index()

        # drop duplicates
        property_df.drop_duplicates(subset=['formula'], keep=False, inplace=True)

        return property_df

